/*
https://leetcode.com/problems/longest-univalue-path/description/

Given a binary tree, find the length of the longest path where each node in the path has the same value.
This path may or may not pass through the root.

Note: The length of path between two nodes is represented by the number of edges between them.

Example 1:
    Input:
                  5
                 / \
                4   5
               / \   \
              1   1   5
    Output:
    2
Example 2:
    Input:
                  1
                 / \
                4   5
               / \   \
              4   4   5
    Output:
    2
Note: The given binary tree has not more than 10000 nodes. The height of the tree is not more than 1000.
*/

package main

import "fmt"

func longestUnivaluePath(root *TreeNode) int {
  arr := dfs687(root)
  res := arr[0]
  if arr[1] > res {
    res = arr[1]
  }
  return res
}

// 递归思路，每个节点结算两个结果：
// 1: 以此节点结束的最长路径
// 2: 不以此节点结束的最长路径
func dfs687(root *TreeNode) []int {
  if root == nil {
    return []int{0, 0}
  }

  lr, rr := dfs687(root.Left), dfs687(root.Right)

  pathEndsHereLeft, pathNotEndsHereLeft := 0, 0
  if root.Left != nil {
    if root.Val == root.Left.Val {
      pathEndsHereLeft, pathNotEndsHereLeft = 1+lr[0], lr[1]
    } else {
      pathNotEndsHereLeft = findMax(lr[0], lr[1])
    }
  }

  pathEndsHereRight, pathNotEndsHereRight := 0, 0
  if root.Right != nil {
    if root.Val == root.Right.Val {
      pathEndsHereRight, pathNotEndsHereRight = 1+rr[0], rr[1]
    } else {
      pathNotEndsHereRight = findMax(rr[0], rr[1])
    }
  }

  return []int{
    findMax(pathEndsHereLeft, pathEndsHereRight),
    findMax(pathEndsHereLeft+pathEndsHereRight, pathNotEndsHereLeft, pathNotEndsHereRight),
  }
}

func findMax(arr ...int) int {
  max := 0
  for _, n := range arr {
    if n > max {
      max = n
    }
  }
  return max
}

