// 返回最长的path
func maxpath(root *TreeNode, path *int) int {
  if root == nil {
    return 0
  }

  left := maxpath(root.Left, path)
  right := maxpath(root.Right, path)

  if root.Left != nil && root.Left.Val == root.Val {
    left++
  } else {
    left = 0
  }

  if root.Right != nil && root.Right.Val == root.Val {
    right++
  } else {
    right = 0
  }

  join := left + right
  if join > *path {
    *path = join
  }

  res := right
  if left > right {
    res = left
  }
  return res
}
